import{_ as t,c as x,o as l,j as n}from"./chunks/framework.rTUm5mJw.js";const c=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"Linux/Linux——系统架构：x86、x64、AMD64、ARM概念解析.md","filePath":"Linux/Linux——系统架构：x86、x64、AMD64、ARM概念解析.md"}'),i={name:"Linux/Linux——系统架构：x86、x64、AMD64、ARM概念解析.md"};function _(a,e,r,u,o,s){return l(),x("div",null,e[0]||(e[0]=[n("p",null,"x86的32位架构一般又被称作IA-32，全名为“Intel Architecture, 32-bit”。其64位架构由AMD率先推出，并被称为“AMD64”。之后也被Intel采用，被其称为“Intel 64”。一般也被称作“x86-64”、“x64”。复杂指令集的代表。英特尔芯片采用",-1),n("p",null,'x86 也被俗称为 "32 位"，因为它也必须在 32 位 RAM 限制内运行，这意味着 x86 CPU 的系统内存不能超过 4GB。这也成为了非常大的一个限制',-1),n("p",null,"x64 是一种复杂指令集架构（CISC），是目前 Windows 和 Linux PC 的主流架构。实际上，它也被称为 x86-64，因为它是建立在原始 x86 指令集的基础上，而不是完全取代它。它于 2000 年由AMD首次发布，引入了两种运行模式：64 位模式和兼容模式，后者允许用户运行 16 位和 32 位应用程序。由于整个 x86 指令集仍在 x64 中执行，因此旧版可执行文件在运行时几乎不会降低性能。所以这种演进最大程度接续了老的软件生态和用户数据资源。。这种演进是合理的，可以保持不同架构之间的软件兼容性。",-1),n("ul",null,[n("li",null,"ARM 精简指令集 常用于移动端 高通芯片 苹果M系列芯片"),n("li",null,"RISV RISC-V是一个基于精简指令集的开源指令集架构。")],-1)]))}const p=t(i,[["render",_]]);export{c as __pageData,p as default};
